public class hw1 {
    public static void main(String[] args) {
        System.out.println("         BROWNIE RECIPE");
        System.out.println();
        System.out.println("INGREDIENTS:");
        System.out.println();
        System.out.println("- 1/2 cup butter");
        System.out.println("- 1 cup white sugar");
        System.out.println("- 2 eggs");
        System.out.println("- 1 teaspoon vanilla extract");
        System.out.println("- 1/3 cup unsweetened cocoa powder");
        System.out.println("- 1/2 cup all-purpose flour");
        System.out.println("- 1/4 teaspoon salt");
        System.out.println("- 1/4 teaspoon baking powder");
        System.out.println();
        System.out.println("For Frosting:");
        System.out.println();
        System.out.println("-- 3 tablespoons butter, softened");
        System.out.println("-- 3 tablespoons unsweetened cocoa powder");
        System.out.println("-- 1 tablespoon honey");
        System.out.println("-- 1 teaspoon vanilla extract");
        System.out.println("-- 1 cup confectioners' sugar");
        System.out.println("Directions: ");
        System.out.println("- Preheat oven to 350 degrees F (175 degrees C). Grease and flour an 8-inch square pan.");
        System.out.print("- In a large saucepan, melt 1/2 cup butter. Remove from heat, and stir in sugar, eggs,");
        System.out.print("and 1 teaspoon vanilla. Beat in 1/3 cup cocoa, 1/2 cup flour, salt, and baking powder.");
        System.out.println("- Spread batter into prepared pan.");
        System.out.println("- Bake in preheated oven for 25 to 30 minutes. Do not overcook.");
        System.out.print("- Remove brownies from the oven, and make frosting. Combine 3 tablespoons softened butter,");
        System.out.println(
                "3 tablespoons cocoa, honey, 1 teaspoon vanilla extract, and 1 cup confectioners' sugar. Stir until smooth.");
        System.out.println("- Frost brownies while they are still warm.");
        System.out.println();
        System.out.println();
        System.out.println("Enjoy!");
    }
}